$(document).ready(function(){
	$.validator.addMethod("UserMobile",function(e,r){return this.optional(r)||e.match(/^[0-9,\)(+-]+$/)},"Please enter valid mobile number."),
	$("#loginform").validate({
		rules:{
			UserEmail:{required:!0,email:!0},
			UserPassword:{required:!0,minlength:6}},
		messages:{
			UserEmail:{
				required:"Please enter your email address.",
				email:"Your email address is invalid."},
			UserPassword:{
				required:"Please enter your password.",
				minlength:"Your password must be at least 6 characters long."}}}),
	$("#forgotform").validate({
		rules:{
			UserEmail:{required:!0,email:!0}},
		messages:{
			UserEmail:{
				required:"Please enter your email address.",
				email:"Your email address is invalid."}}}),
	$("#registerform").validate({
		rules:{
			UserFirstName:{required:!0},
			UserLastName:{required:!0},
			UserEmail:{required:!0,email:!0},
			UserMobile:{required:!0,minlength:10},
			UserAddress:{required:!0},
			country:{required:!0},
			city_state:{required:!0},
			city_state1:{required:!0},
			UserPassword:{required:!0,minlength:6},
			UserConfirmPassword:{required:!0,minlength:6,equalTo:".UserPassword"}},
		messages:{
			UserFirstName:{required:"Please enter your name."},
			UserLastName:{required:"Please enter your last name."},
			country:{required:"Please select the country."},
			city_state:{required:"Please select the state."},
			city_state1:{required:"Please enter the state."},
			UserEmail:{
				required:"Please enter your email address.",
				email:"Your email address is invalid."},
			UserAddress:{required:"Please enter the address."},
			UserMobile:{
				required:"Please enter your mobile.",
				number:"Please enter valid number.",
				minlength:"Mobile number must at least 10 characters."},
				UserPassword:{
					required:"Please enter your password.",
					minlength:"Password must at least 6 characters."},
				UserConfirmPassword:{
					required:"Please enter your confirm password.",
					minlength:"Password must at least 6 characters.",
					equalTo:"These passwords don't match."}}}),
	$("#changepasswordform").validate({
		rules:{
			UserPassword:{required:!0,minlength:6},
			UserNewPassword:{required:!0,minlength:6},
			UserConfirmPassword:{required:!0,minlength:6,equalTo:".UserNewPassword"}},
		messages:{
			UserPassword:{
				required:"Please enter your password.",
				minlength:"Password must at least 6 characters."},
			UserNewPassword:{
				required:"Please enter new password.",
				minlength:"Password must at least 6 characters."},
			UserConfirmPassword:{
				required:"Please enter your confirm password.",
				minlength:"Password must at least 6 characters.",
				equalTo:"Confirm password doesn't match."}}}),
	$("#mailchimpform").validate({
		rules:{
			mailchimpemail:{required:true}
			},
		messages:{
			mailchimpemail:{required:"Please enter your email to join us."}
			}
		}),
	$("#Editprofile").validate({
		rules:{
			Uname:{required:!0},
			Uaddress:{required:!0},
			Umobile:{required:!0,minlength:10},
			Uabout:{required:!0,minlength:50}},
		messages:{
			Uname:{required:"Please enter the your name ."},
			Umobile:{
				required:"Please enter the your mobile number.",
				minlength:"Mobile number must at least 10 characters.."},
				Uabout:{
					required:"Please enter the some description about you.",
					minlength:"About description must at least 50 characters.."},
				Uaddress:{required:"Please enter your address."}}})});
	